import pytest
from EVA.windows.srim import trim_window
from EVA.windows.srim.trim_model import TrimModel
from EVA.windows.srim.trim_window import TrimWindow
from srim import Ion, Layer

class TestSRIM:
    # will run once before every test
    @pytest.fixture(autouse=True)
    def setup(self):
        # use function below to set up sample
        sample_layers, sample_names, total_thickness, sample_density = self.make_setupsample()

        # create trim model instance
        self.trim_model = TrimModel()

        # set sample values in the model
        self.trim_model.sample_layers = sample_layers
        self.trim_model.sample_names = sample_names
        self.trim_model.total_thickness = total_thickness

    def make_setupsample(self):
        #setting up default layer

        i = 0
        sample_layers = []
        sample_names = []
        TotalThickness = 0.0
        sampledensity = 1.0
        for i in range(4):
            if i == 0 :
                sampleName = 'Beamline Window'
                LayerThickness = 0.05
            elif i == 1:
                sampleName = 'Air (compressed)'
                LayerThickness = 0.067
            elif i == 2:
                sampleName = 'Al'
                LayerThickness = 0.05
                sampledensity = 2.7
            else:
                sampleName = 'Cu'
                LayerThickness = 0.5
                sampledensity = 6.7

            if sampleName == 'Beamline Window':
                #LayerThickness = float(self.tab1.table_TRIMsetup.item(i, 1).text())
                TotalThickness = + LayerThickness
                beamwindow = Layer({'H': {'stoich': 8, 'E_d': 10, 'lattice': 3, 'surface': 2
                                          },
                                    'C': {'stoich': 10, 'E_d': 28.0, 'lattice': 3.0,
                                          'surface': 7.41
                                          },
                                    'O': {'stoich': 4, 'E_d': 28.0, 'lattice': 3.0,
                                          'surface': 2.0}},
                                   density=1.4, width=LayerThickness * 1e7, phase=0)
                sample_layers.append(beamwindow)
                sample_names.append('Beamline Window')

            elif sampleName == 'Air (compressed)':

                #LayerThickness = float(self.tab1.table_TRIMsetup.item(i, 1).text())
                TotalThickness = + LayerThickness

                air = Layer({'C': {'stoich': 1.24e-2, 'E_d': 28.0, 'lattice': 3.0,
                                   'surface': 7.41
                                   },
                             'O': {'stoich': 23.1781, 'E_d': 28.0, 'lattice': 3.0,
                                   'surface': 2.0
                                   },
                             'N': {'stoich': 75.5268, 'E_d': 28.0, 'lattice': 3.0,
                                   'surface': 2.0
                                   },
                             'Ar': {'stoich': 1.2827, 'E_d': 5.0, 'lattice': 1.0,
                                    'surface': 2.0}}, density=1500 * 1.20479e-3, width=LayerThickness * 1e7,
                            # air layer compressed from 150mm to 0.1mm to optimise bins
                            phase=1)
                sample_layers.append(air)
                sample_names.append('Air (compressed)')
                #print ('done air')
                #print(sample_layers)
            else:
                #sampledensity = float(self.tab1.table_TRIMsetup.item(i, 2).text())
                #samplethickness = float(self.tab1.table_TRIMsetup.item(i, 1).text())
                TotalThickness = + LayerThickness
                thislayer = Layer.from_formula(sampleName, density=sampledensity, width=LayerThickness * 1e7,
                                               phase=0)

                sample_layers.append(thislayer)  # 0.016 mm = 160000Athick sample holder
                sample_names.append(sampleName)

        return sample_layers, sample_names, TotalThickness, sampledensity

    def test_getxpos(self):
        correct_res = [0.0, 0.05, 0.067, 0.05, 0.5]
        res = self.trim_model.getxpos()
        assert correct_res == res, 'xposlist failed'

